import Meals from "./components/Meals";
import MealsSummary from "./components/MealsSummary";
import Header from './components/Header'
import { useContext, useState } from "react";
import PopupContext from "./store/Popup-context";
import Popup from "./components/UI/Popup";
const DUMMY_MEALS = [
  {
    name: "Sushi",
    description: "Finest fish and veggies",
    price: 22.9,
    id: Math.random().toString(),
  },
  {
    name: "Schnitzel",
    description: "A german speciality",
    price: 16.5,
    id: Math.random().toString(),
  },
  {
    name: "Barbecue Burger",
    description: "American, raw, meaty",
    price: 12.99,
    id: Math.random().toString(),
  },
  {
    name: "Green Bowl",
    description: "Healthy...and green...",
    price: 18.99,
    id: Math.random().toString(),
  },
];

function App() {
  const [enterMeal, setEnterMeal] = useState(0)
  const getMeal = (enteredMeal) => {
    setEnterMeal(+enterMeal + +enteredMeal)
  }
  const ctx = useContext(PopupContext);
  console.log(enterMeal);
  console.log(ctx.isOpenPopup);

  return (
    <div>
      <Header meal={enterMeal}></Header>
      <MealsSummary></MealsSummary>
      <Meals meals={DUMMY_MEALS} onGetMeal={getMeal}></Meals>
      {ctx.isOpenPopup && <Popup></Popup>}
    </div>
  );
}

export default App;
